import { useContext,useState } from 'react';
import {Box, Button, TextField,Typography,styled} from '@mui/material';
import { API } from '../../service/api.js';
import { DataContext } from '../../context/DataProvider.jsx';
import { useNavigate } from 'react-router-dom';
const Component = styled(Box)`
    width: 400px;
    margin: auto;
    box-shadow: 5px 2px 5px 2px rgb(0 0 0/ 0.6);
`;
const Image = styled('img')({
    width: 100,
    display: 'flex',
    margin: 'auto',
    padding: '50px 0 0'
});
const Wrapper = styled(Box)`
    padding: 25px 35px;
    display: flex;
    flex: 1;
    overflow: auto;
    flex-direction: column;
    & > div, & > button, & > p {
        margin-top: 20px;
    }
`;
const LoginButton = styled(Button)`
    text-transform: none;
    background: #FB641B;
    color: #fff;
    height: 48px;
    border-radius: 2px;
`;
const SignupButton = styled(Button)`
    text-transform: none;
    background: #fff;
    color: #2874f0;
    height: 48px;
    border-radius: 2px;
    box-shadow: 0 2px 4px 0 rgb(0 0 0 / 20%);
`;

const Text = styled(Typography)`
    color: #878787;
    font-size: 12px;
`;

const Error = styled(Typography)`
    font-size: 10px;
    color: #ff6161;
    line-height: 0;
    margin-top: 10px;
    font-weight: 600;
`

const loginInitialValues = {
    username: '',
    password: ''
};

const signupInitialValues = {
    name: '',
    username: '',
    password: ''
};
const Login =({isUserAuthenticated})=>{

    const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';
    const [account, toggleAccount] = useState('login');
    const [login, setLogin] = useState(loginInitialValues);
    const [singup, setSignup] = useState(signupInitialValues);
    const [error,setError] =useState('');
    const {setAccount}=useContext(DataContext);    
    const navigate=useNavigate();
    const onInputChange = (e) => {
        setSignup({ ...singup, [e.target.name]: e.target.value });
    }
    const signupUser = async () => {
        try {
            let response = await API.userSignup(signup);
    
            if (response.isSuccess) {
                setError('');
                setSignup(signupInitialValues);
                toggleAccount('login');
            } else {
                // Check for specific error cases
                if (response.errorCode === 'USER_ALREADY_EXISTS') {
                    setError('User with this email already exists. Please use a different email.');
                } else if (response.errorCode === 'INVALID_EMAIL_FORMAT') {
                    setError('Invalid email format. Please enter a valid email address.');
                } else {
                    // Handle other general errors
                    setError('Something went wrong! Please try again later.');
                }
            }
        } catch (error) {
            console.error('Error during signup:', error);
            // Handle unexpected errors
            setError('An unexpected error occurred. Please try again later.');
        }
    };
    
    const onValueChange = (e) => {
        setLogin({ ...login, [e.target.name]: e.target.value });
    }
    const loginUser= async()=>{
        try{
            let response=await API.userLogin(login);
            if(response.isSuccess)
            {   setError('');
                sessionStorage.setItem('accessToken',`Bearer ${response.data.accessToken}`);
                sessionStorage.setItem('refreshToken',`Bearer ${response.data.refreshToken}`);
                setAccount({username:response.data.username,name:response.data.name});
                isUserAuthenticated(true);
                navigate('/');
            }
            else 
            {   console.error('ERROR IN RESPONSE:', response.error);
                setError('Login failed. Please check your credentials.');
            }
         }
        catch(err)
        {   console.log(error);   
            console.error('Unexpected error:', err);
            setError('Something went wrong! Please try again later.');
        }
    }
    const toggleSignup = () => {
        account === 'signup' ? toggleAccount('login') : toggleAccount('signup');
    }
    return(
        <Component>
            <Box>
                <Image src={imageURL} alt="login"/>
                {
                    account==='login'?
                        <Wrapper>
                           <TextField variant="standard" value={login.username} onChange={(e)=>onValueChange(e)} name='username' label="Enter Username"/>
                            <TextField variant="standard" value={login.password} onChange={(e)=>onValueChange(e)} name='password' label="Enter Password"/>
                            { error && <Error>{error}</Error>}
                            <LoginButton variant="contained" onClick={()=>loginUser()}>Login</LoginButton>
                            <Text style={{textAlign:'center'}}>OR</Text>
                            <SignupButton onClick={()=>toggleSignup()}>Create an account</SignupButton>
                        </Wrapper>
                    :
                        <Wrapper>
                            <TextField variant="standard" onChange={(e)=>onInputChange(e)} name='name' label="Enter Name"/>
                            <TextField variant="standard" onChange={(e)=>onInputChange(e)} name='username' label="Enter Username"/>
                            <TextField variant="standard" onChange={(e)=>onInputChange(e)} name='password' label="Enter Password"/>
                            { error && <Error>{error}</Error>}
                            <SignupButton onClick={()=>signupUser()}>Signup</SignupButton>
                            <Text style={{textAlign:'center'}}>OR</Text>
                            <LoginButton variant="contained" onClick={()=>toggleSignup()}>Already have an account</LoginButton>
                        </Wrapper>
                }
            </Box>
        </Component>        
        )
}
export default Login;
